package Basic;

public class datatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10; 
		int b = 20;
		int c = a+b; 
		System.out.println("\nSum of two integer is:" +c);
		
		float first = 1.2f;
		float second = 2.2f;
		float third = 1.3f;
		float sum = first+second+third; 
		System.out.println("\nSum of three floats is:"+sum);
		
		Double fisdl = 11.22;
		Double secdl = 22.11;
		Double sumdl = fisdl+secdl;
		System.out.println("\nSum of two Double is:"+sumdl);
	
			
		
	

	}

}
